import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      redirect: null, 
      userReady: false,
      currentUser: { username: "" }
    };
  }

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) this.setState({ redirect: "/home" });
    this.setState({ currentUser: currentUser, userReady: true })
  }

  render() {
    if (this.state.redirect) {
      return <Navigate to={this.state.redirect} />
    }

    const { currentUser } = this.state;

    const containerStyle = {
      maxWidth: '800px',
      margin: 'auto',
      padding: '20px',
      boxShadow: '0 0 10px rgba(0,0,0,0.1)'
    };

    const jumbotronStyle = {
      padding: '2rem 1rem',
      marginBottom: '2rem',
      backgroundColor: '#f9f9f9',
      borderRadius: '0.3rem'
    };

    const headerStyle = {
      fontSize: '1.5rem'
    };

    return (
      <div style={containerStyle}>
        {this.state.userReady ? (
          <div>
            <header style={jumbotronStyle}>
              <h3 style={headerStyle}>
                Bok, {currentUser.username} !
              </h3>
            </header>
            <p>Dobrodošli u <b>Pien!</b></p>
            <p>Ovdje možete pregledati svoje podatke, pratiti narudžbe i upravljati postavkama profila.</p>
            <ul>
              <li>Pregledajte najnovije proizvode</li>
              <li>Pratite status svojih narudžbi</li>
              <li>Pregledajte i ažurirajte svoje postavke</li>
              <li>Kontaktirajte korisničku podršku</li>
            </ul>
          </div>
        ) : null}
      </div>
    );
  }
}
