// AddProduct.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AddProduct.css';

function AddProduct({ onProductAdded }) {
  const [name, setName] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [description, setDescription] = useState('');
  const [sizeOptions, setSizeOptions] = useState([]);
  const [selectedSize, setSelectedSize] = useState('');
  const [price, setPrice] = useState(0);
  const [colorOptions, setColorOptions] = useState([]);
  const [selectedColor, setSelectedColor] = useState('');
  const [genderOptions, setGenderOptions] = useState([]);
  const [selectedGender, setSelectedGender] = useState('');
  const [categoryOptions, setCategoryOptions] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    axios.get('http://localhost:8080/sizes')
      .then(response => {
        setSizeOptions(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    axios.get('http://localhost:8080/colors')
      .then(response => {
        setColorOptions(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    axios.get('http://localhost:8080/genders')
      .then(response => {
        setGenderOptions(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    axios.get('http://localhost:8080/productscategory')
      .then(response => {
        setCategoryOptions(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleFileChange = (event) => {
    setImageFile(event.target.files[0]);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('file', imageFile);

    const selectedColorObj = colorOptions.find(color => color.id === parseInt(selectedColor));
    const selectedSizeObj = sizeOptions.find(size => size.id === parseInt(selectedSize));
    const selectedGenderObj = genderOptions.find(gender => gender.id === parseInt(selectedGender));
    const selectedCategoryObj = categoryOptions.find(category => category.id === parseInt(selectedCategory));

    const productData = {
      name,
      description,
      size: selectedSizeObj,
      price,
      color: selectedColorObj,
      gender: selectedGenderObj,
      category: selectedCategoryObj,
    };

    try {
      const response = await axios.post('http://localhost:8080/products', productData);
      const productId = response.data.id;

      const uploadResponse = await axios.post(`http://localhost:8080/products/upload/${productId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const imageURL = uploadResponse.data.imageURL;
      const newProduct = {
        ...productData,
        imageURL,
      };

      onProductAdded(newProduct); // Dodajte novi proizvod na popis proizvoda

      // Resetirajte formu
      setName('');
      setImageFile(null);
      setDescription('');
      setSelectedSize('');
      setPrice(0);
      setSelectedColor('');
      setSelectedGender('');
      setSelectedCategory('');
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dodavanja proizvoda.');
    }
  };

  return (
    <div className="form-container">
      <h2>Dodaj proizvod</h2>
      <form onSubmit={handleFormSubmit}>
        {/* Ostatak forme */}
      </form>
    </div>
  );
}

export default AddProduct;
