import React, { useState, useEffect } from "react";
import axios from 'axios';
import authHeader from './services/auth-header';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const [cart, setCart] = useState([]);
  const [viewingImage, setViewingImage] = useState(null); // State for the image being viewed in the modal
  const user = JSON.parse(localStorage.getItem('user'));
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      try {
        if (user && user.id) {
          const response = await axios.get(`http://localhost:8080/orders/${user.id}`, { headers: authHeader() });
          if (response.data) {
            setCart(response.data);
          }
        }
      } catch (error) {
        console.error('Error fetching the cart', error);
      }
    };

    fetchCart();
  }, []);

  const updateQuantity = async (id, quantity) => {
    try {
      await axios.put(`http://localhost:8080/orders/${user.id}/${id}`, { quantity }, { headers: authHeader() });
      setCart(cart.map(item => item.id === id ? {...item, quantity} : item));
    } catch (error) {
      console.error('Error updating quantity', error);
    }
  }

  const completeOrder = () => {
    // Simply redirect the user to the order success page
    navigate('/order-success');
  }

  const showImage = (imageUrl) => {
    setViewingImage(imageUrl);
  };

  const closeImageModal = () => {
    setViewingImage(null);
  };

  if (!cart || cart.length === 0) {
    return <p>Jos uvijek nemate narudzbi.</p>;
  }

  const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  return (
    <div className="container mt-5">
      <h2>Your Cart</h2>

      {/* Image Modal */}
      {viewingImage && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundColor: 'rgba(0,0,0,0.7)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center'
        }} 
        onClick={closeImageModal}>
          <img src={viewingImage} alt="Full View" style={{maxWidth: '80%', maxHeight: '80%'}} />
        </div>
      )}

      <table className="table mt-4">
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>Price per unit</th>
          </tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={item.id}>
              <td>
                <img 
                  src={`http://localhost:8080/products/images/${item.product.imageURL}`} 
                  alt={item.product.name}
                  style={{width: '100px', cursor: 'pointer'}} 
                  onClick={() => showImage(`http://localhost:8080/products/images/${item.product.imageURL}`)}
                />
               
              </td>
              <td>
              {item.product.name}
              </td>
              <td>
                <div style={{display: 'flex', flexDirection: 'row'}}>
                  <button 
                    style={{marginRight: '5px', width: '30px', height: '30px'}} 
                    onClick={() => updateQuantity(item.id, Math.max(1, item.quantity - 1))}
                  >
                    -
                  </button>
                  <span> {item.quantity} </span>
                  <button 
                    style={{marginLeft: '5px', width: '30px', height: '30px'}} 
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                  >
                    +
                  </button>
                </div>
              </td>
              <td>{item.price.toFixed(2)}€</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3 className="mt-4">Total: {total.toFixed(2)}€</h3>
      
      <button onClick={completeOrder} style={{marginTop: '20px'}}>Završi narudžbu</button>
    </div>
  );
};

export default Cart;
