import React, { useState } from 'react';
import axios from 'axios';

const loginUser = async (email, password) => {
    try {
      const response = await axios.post('/users/login', {
        email: email,
        password: password,
      });
      console.log(response.data); // Možete obraditi odgovor na odgovarajući način
    } catch (error) {
      console.error(error);
    }
  };
const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = () => {
    loginUser(email, password);
  };

  return (
    <div>
      <h2>Login</h2>
      E-mail:<input type="text" placeholder="Email" value={email} onChange={handleEmailChange} /><br></br>
      Password:<input type="password" placeholder="Password" value={password} onChange={handlePasswordChange} />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default LoginForm;
