import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

function ProductDetails() {
  const { id } = useParams();
  const history = useHistory();
  const [product, setProduct] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Ovdje se pretpostavlja da je 'user' objekt pohranjen u localStorage
    // i ima svojstvo 'roles' koje je niz.
    const user = JSON.parse(localStorage.getItem('user'));
    if (user && user.roles.includes('ROLE_ADMIN')) {
      setIsAdmin(true);
    }
  }, []);

  useEffect(() => {
    const fetchProduct = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`http://localhost:8080/api/products/${id}`);
        setProduct(response.data);
      } catch (error) {
        console.error('There was an error fetching the product details:', error);
      }
      setIsLoading(false);
    };

    fetchProduct();
  }, [id]);

  const addToCart = () => {
    // Ovdje možete implementirati logiku za dodavanje proizvoda u košaricu.
    // Na primjer, možete koristiti Redux ili lokalno skladište za praćenje stavki u košarici.
    alert('Proizvod dodan u košaricu');
  };

  const editProduct = () => {
    // Ovdje se preusmjerava na stranicu za uređivanje proizvoda
    history.push(`/edit-product/${id}`);
  };

  const deleteProduct = async () => {
    try {
      await axios.delete(`http://localhost:8080/api/products/${id}`);
      alert('Proizvod uspješno obrisan');
      history.push('/products'); // Preusmjeravanje na popis proizvoda
    } catch (error) {
      console.error('There was an error deleting the product:', error);
    }
  };

  if (isLoading) {
    return <div>Učitavanje...</div>;
  }

  if (!product) {
    return <div>Proizvod nije pronađen</div>;
  }

  return (
    <div>
      <h1>{product.name}</h1>
      <img src={product.imageURL} alt={product.name} />
      <p>{product.description}</p>
      <p>Cijena: {product.price} €</p>
      <button onClick={addToCart}>Dodaj u košaricu</button>
      {isAdmin && (
        <div>
          <button onClick={editProduct}>Uredi</button>
          <button onClick={deleteProduct}>Obriši</button>
        </div>
      )}
    </div>
  );
}

export default ProductDetails;
