import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../ProductList.css';
import authHeader from '../services/auth-header';

function ProductList() {
    const [products, setProducts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [productsPerPage, setProductsPerPage] = useState(10);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [productCategories, setProductCategories] = useState([]);
    const [colors, setColors] = useState([]);
    const [sizes, setSizes] = useState([]);
    const [genders, setGenders] = useState([]);
    const [orderId, setOrderId] = useState(localStorage.getItem('orderId'));
    const [isModalOpen, setModalOpen] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [isAdmin, setIsAdmin] = useState(false);
    const [sortType, setSortType] = useState('none');
    const [formFields, setFormFields] = useState({
        name: '',
        description: '',
        price: '',
        color: '',
        size: '',
        gender: '',
        imageURL: ''
    });

    useEffect(() => {
        let sortedProducts = [...products];
        if (sortType === 'asc'){
            sortedProducts.sort((a,b) => a.price - b.price);
        }
        else if (sortType === 'desc'){
            sortedProducts.sort((a,b) => b.price - a.price);
        }
        setProducts(sortedProducts);
    }, [sortType]);

    useEffect(() => {
        const user = JSON.parse(localStorage.getItem('user'));
        if (user && user.roles && user.roles.includes('ROLE_ADMIN')) {
            setIsAdmin(true);
        }
    }, []);

    useEffect(() => {
        fetchProducts();
        fetchProductCategories();
        fetchColors();
        fetchSizes();
        fetchGenders();
    }, [currentPage]);

    useEffect(() => {
        if (selectedCategory) {
            fetchProductsByCategory(selectedCategory);
        } else {
            fetchProducts();
        }
    }, [selectedCategory, currentPage]);

    useEffect(() => {
        console.log("isModalOpen changed:", isModalOpen);
    }, [isModalOpen]);

    const fetchProducts = async () => {
        const response = await axios.get(`http://localhost:8080/products?page=${currentPage-1}&size=${productsPerPage}`, {
            headers: authHeader(),
        });
        setProducts(response.data);
    };

    const fetchProductsByCategory = async (category) => {
        const response = await axios.get(`http://localhost:8080/products?category=${category}&page=${currentPage-1}&size=${productsPerPage}`, {
            headers: authHeader(),
        });
        setProducts(response.data);
    };

    const fetchProductCategories = async () => {
        const response = await axios.get('http://localhost:8080/productscategory', {
            headers: authHeader(),
        });
        setProductCategories(response.data);
    };

    const fetchColors = async () => {
        const response = await axios.get('http://localhost:8080/colors', {
            headers: authHeader(),
        });
        setColors(response.data);
    };

    const fetchSizes = async () => {
        const response = await axios.get('http://localhost:8080/sizes', {
            headers: authHeader(),
        });
        setSizes(response.data);
    };

    const fetchGenders = async () => {
        const response = await axios.get('http://localhost:8080/genders', {
            headers: authHeader(),
        });
        setGenders(response.data);
    };

    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
    };

    // ... (remaining methods remain the same)

    return (
        <div>
            <h2>Popis proizvoda</h2>
            {/* ... (remaining HTML remains the same) */}
            <div className="product-list">
                {products.map((product) => (
                    <div key={product.id} className="product-item">
                        {/* ... (rest of the product rendering logic) */}
                    </div>
                ))}
            </div>
            <div className="pagination">
                {[...Array(Math.ceil(products.length / productsPerPage)).keys()].map((page, index) => (
                    <button key={index} onClick={() => handlePageChange(page + 1)}>
                        {page + 1}
                    </button>
                ))}
            </div>
            <Link to="/cart">Prikaži košaricu</Link>
        </div>
    );
}

export default ProductList;
