import React, { useState } from 'react';
import axios from 'axios';

function AddProductCategory() {
  const [name, setName] = useState('');
  const [image, setImage] = useState(null);

  const handleNameChange = e => {
    setName(e.target.value);
  };

  const handleImageChange = e => {
    const file = e.target.files[0];
    setImage(file);
  };

  const handleSubmit = e => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', name);
    formData.append('image', image);

    axios
      .post('http://localhost:8080/productscategory', formData)
      .then(response => {
        console.log(response.data);
        alert('Product category successfully added.');
        setName('');
        setImage(null);
      })
      .catch(error => {
        console.error(error);
        alert('An error occurred while adding the product category.');
      });
  };

  return (
    <div className="add-product-category">
      <h1>Add Product Category</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={handleNameChange} />
        </div>
        <div>
          <label>Image:</label>
          <input type="file" accept="image/*" onChange={handleImageChange} />
        </div>
        <button type="submit">Add Product Category</button>
      </form>
    </div>
  );
}

export default AddProductCategory;
