import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from './services/auth-header';

function AllOrders() {

    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
  
    useEffect(() => {
      fetchAllOrders();
    }, []);
  
    const fetchAllOrders = async () => {
      try {
        const response = await axios.get('http://localhost:8080/orderDetails/all', {
          headers: authHeader(),
        });
        setOrders(response.data);
      } catch (error) {
        console.error('Došlo je do pogreške prilikom dohvaćanja svih narudžbi:', error);
        setError(error.message);
      }
    };
  
    return (
      <div>
        <h2>Popis svih narudžbi</h2>
        <div className="order-list">
          {error && <p>Došlo je do pogreške: {error}</p>}
          {orders.map((order) => (
            <div key={order.id} className="order-item">
              {/* Možete dodati detalje narudžbi kako želite. 
                   Ovo je samo primjer, prilagodite prema vašim potrebama: */}
              <h3>Narudžba ID: {order.id}</h3>
              <p>Proizvod: {order.productName}</p>
              <p>Količina: {order.quantity}</p>
              <p>Cijena: {order.price}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }
  
  export default AllOrders;
