import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AddProduct.css';

function AddProduct() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    imageURL: '',
    description: '',
    price: 0,
    color: null,
    size: null,
    gender: null,
    category: null,
  });
  const [sizeOptions, setSizeOptions] = useState([]);
  const [colorOptions, setColorOptions] = useState([]);
  const [genderOptions, setGenderOptions] = useState([]);
  const [categoryOptions, setCategoryOptions] = useState([]);

  useEffect(() => {
    fetchSizeOptions();
    fetchColorOptions();
    fetchGenderOptions();
    fetchCategoryOptions();
  }, []);

  const fetchSizeOptions = async () => {
    try {
      const response = await axios.get('http://localhost:8080/sizes');
      setSizeOptions(response.data);
    } catch (error) {
      console.error(error);
      alert('Failed to fetch size options.');
    }
  };

  const fetchColorOptions = async () => {
    try {
      const response = await axios.get('http://localhost:8080/colors');
      setColorOptions(response.data);
    } catch (error) {
      console.error(error);
      alert('Failed to fetch color options.');
    }
  };

  const fetchGenderOptions = async () => {
    try {
      const response = await axios.get('http://localhost:8080/genders');
      setGenderOptions(response.data);
    } catch (error) {
      console.error(error);
      alert('Failed to fetch gender options.');
    }
  };

  const fetchCategoryOptions = async () => {
    try {
      const response = await axios.get('http://localhost:8080/productscategory');
      setCategoryOptions(response.data);
    } catch (error) {
      console.error(error);
      alert('Failed to fetch category options.');
    }
  };

  const addProduct = async () => {
    try {
      const response = await axios.post('http://localhost:8080/products', newProduct);
      const createdProduct = response.data;
      setProducts([...products, createdProduct]);
      setNewProduct({
        name: '',
        imageURL: '',
        description: '',
        price: 0,
        color: null,
        size: null,
        gender: null,
        category: null,
      });
      alert('Product added successfully.');
    } catch (error) {
      console.error(error);
      alert('Failed to add product.');
    }
  };

  const handleChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  return (
    <div className="add-product-container">
      <h2>Add Product</h2>
      <div className="input-group">
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" value={newProduct.name} onChange={handleChange} />
      </div>
      <div className="input-group">
        <label htmlFor="imageURL">Image URL:</label>
        <input type="text" id="imageURL" name="imageURL" value={newProduct.imageURL} onChange={handleChange} />
      </div>
      <div className="input-group">
        <label htmlFor="description">Description:</label>
        <input type="text" id="description" name="description" value={newProduct.description} onChange={handleChange} />
      </div>
      <div className="input-group">
        <label htmlFor="price">Price:</label>
        <input type="number" id="price" name="price" value={newProduct.price} onChange={handleChange} />
      </div>
      <div className="input-group">
        <label htmlFor="size">Size:</label>
        <select id="size" name="size" value={newProduct.size} onChange={handleChange}>
          <option value="">Select a size</option>
          {sizeOptions.map((size) => (
            <option key={size.id} value={size.id}>{size.name}</option>
          ))}
        </select>
      </div>
      <div className="input-group">
        <label htmlFor="color">Color:</label>
        <select id="color" name="color" value={newProduct.color} onChange={handleChange}>
          <option value="">Select a color</option>
          {colorOptions.map((color) => (
            <option key={color.id} value={color.id}>{color.name}</option>
          ))}
        </select>
      </div>
      <div className="input-group">
        <label htmlFor="gender">Gender:</label>
        <select id="gender" name="gender" value={newProduct.gender} onChange={handleChange}>
          <option value="">Select a gender</option>
          {genderOptions.map((gender) => (
            <option key={gender.id} value={gender.id}>{gender.name}</option>
          ))}
        </select>
      </div>
      <div className="input-group">
        <label htmlFor="category">Category:</label>
        <select id="category" name="category" value={newProduct.category} onChange={handleChange}>
          <option value="">Select a category</option>
          {categoryOptions.map((category) => (
            <option key={category.id} value={category.id}>{category.name}</option>
          ))}
        </select>
      </div>
      <button className="add-button" onClick={addProduct}>Add Product</button>
    </div>
  );
}

export default AddProduct;
