import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';
import authHeader from '../services/auth-header';

function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user && user.roles && user.roles.includes('ROLE_ADMIN')) {
      setIsAdmin(true);
    }
  }, []);

  useEffect(() => {
    const fetchProduct = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`http://localhost:8080/products/${id}`, {
          headers: authHeader(),
        });
        setProduct(response.data);
      } catch (error) {
        console.error('There was an error fetching the product details:', error);
      }
      setIsLoading(false);
    };

    fetchProduct();
  }, [id]);

  const addToCart = async (productId, price, quantity = 1) => {
    let currentOrderId = orderId;
    if (!currentOrderId) {
        currentOrderId = await createOrder();  
    }
    const data = {
        quantity: quantity,
        price: price,
    };
    await axios.post(`http://localhost:8080/orderDetails?orderId=${currentOrderId}&productId=${productId}`, data, {
        headers: authHeader(),
    });
    alert(`Proizvod je dodan u košaricu.`);
};
  const deleteProduct = async () => {
    try {
      await axios.delete(`http://localhost:8080/products/${id}`, {
        headers: authHeader(),
      });
      alert('Product deleted successfully.');
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  const editProduct = () => {
    // Navigate to the edit product page
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div>
      <h1>{product.name}</h1>
      <img 
        src={`http://localhost:8080/products/images/${product.imageURL}`} 
        alt={product.name}
      />
      <p>{product.description}</p>
      <p>Size: {product.size?.name}</p>
      <p>Color: {product.color?.name}</p>
      <p>Gender: {product.gender?.name}</p>
      <p>Price: {product.price}€</p>
      <button onClick={addToCart}>Add to Cart</button>
      {isAdmin && (
        <div>
          <button onClick={editProduct}>Edit</button>
          <button onClick={deleteProduct}>Delete</button>
        </div>
      )}
    </div>
  );
}

export default ProductDetails;
