import React, { useState } from 'react';
import axios from 'axios';



function AddProductCategory() {
    const [name, setName] = useState('');
    const [imageURL, setImageURL] = useState('');

    const handleFormSubmit = async (e) => {
        e.preventDefault();
    
        const productData = {
          name,
          imageURL
        };

    axios
      .post('http://localhost:8080/productscategory', productData)
      .then((response) => {
        console.log(response.data);
        alert('Kategorija je uspješno dodan.');
        // Resetirajte formu
        setName('');
      })
      .catch((error) => {
        console.error(error);
        alert('Došlo je do pogreške prilikom dodavanja kategorije.');
      });

    }
    return (
        <div className="form-container"> {/* Dodajte klasu na vanjski div */}
          <h2>Dodaj Kategoriju</h2>
          <form onSubmit={handleFormSubmit}>
            <div>
              <label>Naziv:</label>
              <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
            </div>
            <button type="submit">Dodaj</button>
          </form>
        </div>
      );
    }
    
export default AddProductCategory;
