import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:8080/products');
      setProducts(response.data);
    } catch (error) {
      console.error(error);
      alert('An error occurred while fetching products.');
    }
  };

  const handleOrder = (productId) => {
    // Ovdje možete dodati logiku za narudžbu proizvoda s određenim productId
    // Na primjer, otvoriti modal za unos podataka o narudžbi ili preusmjeriti korisnika na stranicu za narudžbu
    alert(`Order product with ID ${productId}`);
  };

  const handleFileUpload = async (productId, event) => {
    const file = event.target.files[0];

    try {
      const formData = new FormData();
      formData.append('file', file);

      await axios.post(`http://localhost:8080/products/${productId}/upload`, formData);
      alert('Image uploaded successfully!');
    } catch (error) {
      console.error(error);
      alert('An error occurred while uploading the image.');
    }
  };

  return (
    <div>
      <h2>Product List</h2>
      {products.map((product) => (
        <div key={product.id}>
          <h3>{product.name}</h3>
          <img src={product.imageURL} alt={product.name} />
          <p>{product.description}</p>
          <p>Size: {product.size.name}</p>
          <p>Price: {product.price}</p>
          <p>Color: {product.color.name}</p>
          <p>Gender: {product.gender.name}</p>
          <p>Category: {product.category.name}</p>
          <input type="file" accept="image/*" onChange={(event) => handleFileUpload(product.id, event)} />
          <button onClick={() => handleOrder(product.id)}>Order</button>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
