import React, { useState, useEffect } from 'react';
import axios from 'axios';
import placeholderImage from './images/placeholder-image.png';
import './ProductList.css';

function ProductList() {
  // State variables
  const [products, setProducts] = useState([]);
  const [orderItems, setOrderItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [productCategories, setProductCategories] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const userId = 1;

  // Fetch products and product categories on component mount
  useEffect(() => {
    fetchProducts();
    fetchProductCategories();
  }, []);

  // Fetch products based on selected category
  useEffect(() => {
    if (selectedCategory) {
      fetchProductsByCategory(selectedCategory);
    } else {
      fetchProducts();
    }
  }, [selectedCategory]);

  // Fetch all products
  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:8080/products');
      setProducts(response.data);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dohvaćanja proizvoda.');
    }
  };

  // Fetch products by category
  const fetchProductsByCategory = async (category) => {
    try {
      const response = await axios.get(`http://localhost:8080/products?category=${category}`);
      setProducts(response.data);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dohvaćanja proizvoda.');
    }
  };

  // Fetch all product categories
  const fetchProductCategories = async () => {
    try {
      const response = await axios.get('http://localhost:8080/productscategory');
      setProductCategories(response.data);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dohvaćanja kategorija proizvoda.');
    }
  };

  // Add a product to the order
  const addToOrder = (productId) => {
    const product = products.find((product) => product.id === productId);
    const category = product?.category?.name || '';
    setOrderItems([...orderItems, { productId, category }]);
    alert(`Proizvod s ID-om ${productId} dodan u narudžbu.`);
  };

  // Submit the order
  const handleSubmitOrder = async () => {
    const orderData = {
      orderTime: new Date().toISOString(),
      userId: userId,
    };

    try {
      const orderResponse = await axios.post('http://localhost:8080/orders', orderData);

      for (const item of orderItems) {
        const product = products.find((product) => product.id === item.productId);

        const orderDetailsData = {
          orderId: orderResponse.data.id,
          productId: item.productId,
          quantity: 1,
          price: product?.price,
        };

        await axios.post('http://localhost:8080/orderDetails', orderDetailsData);
      }

      setOrderItems([]);

      alert(`Narudžba je uspješno izvršena.`);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom izvršavanja narudžbe.');
    }
  };

  return (
    <div>
      <h2>Popis proizvoda</h2>
      <div className="category-list">
        <button onClick={() => setSelectedCategory('')}>Svi proizvodi</button>
        {productCategories.map((category) => (
          <button key={category.id} onClick={() => setSelectedCategory(category.name)}>
            {category.name}
          </button>
        ))}
      </div>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <h3>{product.name}</h3>
            <img
              src={`${process.env.PUBLIC_URL}/img/${product.image}`}
              alt={product.name}
              className="product-image"
            />
            <p>{product.description}</p>
            <p>Veličina: {product.size?.name}</p>
            <p>Cijena: {product.price}€</p>
            <p>Boja: {product.color?.name}</p>
            <p>Spol: {product.gender?.name}</p>
            <button onClick={() => addToOrder(product.id)}>Dodaj u narudžbu</button>
          </div>
        ))}
      </div>
      {orderItems.length > 0 && (
        <button onClick={handleSubmitOrder}>Izvrši narudžbu</button>
      )}
    </div>
  );
}

export default ProductList;
