import React, { useState, useEffect } from "react";
import axios from 'axios';
import authHeader from './services/auth-header';

const Cart = () => {
  const [cart, setCart] = useState([]);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchCart = async () => {
      try {
        if (user && user.id) {
          const response = await axios.get(`http://localhost:8080/orders/${user.id}`, { headers: authHeader() });
          if (response.data) {
            setCart(response.data);
          }
        }
      } catch (error) {
        console.error('Error fetching the cart', error);
      }
    };

    fetchCart();
  }, []);

  const updateQuantity = async (id, quantity) => {
    try {
      await axios.put(`http://localhost:8080/orders/${user.id}/${id}`, { quantity }, { headers: authHeader() });
      setCart(cart.map(item => item.id === id ? {...item, quantity} : item));
    } catch (error) {
      console.error('Error updating quantity', error);
    }
  }

  if (!cart || cart.length === 0) {
    return <p>Jos uvijek nemate narudzbi.</p>;
  }

  const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  return (
    <div className="container mt-5">
      <h2>Your Cart</h2>

      <table className="table mt-4">
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>Price per unit</th>
          </tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={item.id}>
              <td>{item.product.name}</td>
              <td>
                <input 
                  type="number" 
                  min="1" 
                  value={item.quantity} 
                  onChange={e => updateQuantity(item.id, parseInt(e.target.value))} 
                />
              </td>
              <td>{item.price.toFixed(2)}€</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3 className="mt-4">Total: {total.toFixed(2)}€</h3>
    </div>
  );
};

export default Cart;
