// ProductList.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import placeholderImage from './images/placeholder-image.png';
import './ProductList.css'; // Import the CSS file for styling

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:8080/products');
      setProducts(response.data);
    } catch (error) {
      console.error(error);
      alert('An error occurred while fetching products.');
    }
  };

  const handleOrder = (productId) => {
    // Handle the order logic here
    alert(`Order product with ID ${productId}`);
  };

  const handleProductAdded = (newProduct) => {
    setProducts([...products, newProduct]);
  };

  return (
    <div>
      <h2>Product List</h2>
      {products.map((product) => (
        <div key={product.id} className="product-item">
          <h3>{product.name}</h3>
          <img
            src={product.imageURL || placeholderImage}
            alt={product.name}
            className="product-image"
          />
          <p>{product.description}</p>
          <p>Size: {product.size.name}</p>
          <p>Price: {product.price}</p>
          <p>Color: {product.color.name}</p>
          <p>Gender: {product.gender.name}</p>
          <p>Category: {product.category.name}</p>
          <button onClick={() => handleOrder(product.id)}>Order</button>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
