import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from '../services/auth-header';
import { useParams } from 'react-router-dom'; // Import useParams hook
import '../AllOrders.css';

function OrderDetailsPage() {
    const [orderDetails, setOrderDetails] = useState([]); // renamed from orders to make it clearer
    const [totalPrice, setTotalPrice] = useState(0);
    const { id } = useParams(); // get the id from the URL

    useEffect(() => {
        fetchOrderDetails(id); // fetch details for that specific ID
    }, [id]); // dependency array now contains id

    const fetchOrderDetails = async (orderId) => {
        try {
            const response = await axios.get(`http://localhost:8080/orderDetails/details/order/${orderId}`, {
                headers: authHeader(),
            });
            setOrderDetails(response.data);
            const total = response.data.reduce((acc, orderDetail) => acc + (orderDetail.quantity * orderDetail.product.price), 0);
            setTotalPrice(total);
        } catch (error) {
            console.error('Error fetching order details:', error);
        }
    };

    return (
        <div className="orders-container">
            <h2>Order Details for Order ID: {id}</h2>
            {/* ... rest of your code */}
        </div>
    );
}

export default OrderDetailsPage;
