import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AddProduct.css';

function AddProduct() {
  const [name, setName] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [description, setDescription] = useState('');
  const [sizeOptions, setSizeOptions] = useState([]);
  const [selectedSize, setSelectedSize] = useState('');
  const [price, setPrice] = useState(0);
  const [colorOptions, setColorOptions] = useState([]);
  const [selectedColor, setSelectedColor] = useState('');
  const [genderOptions, setGenderOptions] = useState([]);
  const [selectedGender, setSelectedGender] = useState('');
  const [categoryOptions, setCategoryOptions] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    fetchOptions();
  }, []);

  const fetchOptions = async () => {
    try {
      const sizeResponse = await axios.get('http://localhost:8080/sizes');
      setSizeOptions(sizeResponse.data);

      const colorResponse = await axios.get('http://localhost:8080/colors');
      setColorOptions(colorResponse.data);

      const genderResponse = await axios.get('http://localhost:8080/genders');
      setGenderOptions(genderResponse.data);

      const categoryResponse = await axios.get('http://localhost:8080/productscategory');
      setCategoryOptions(categoryResponse.data);
    } catch (error) {
      console.error(error);
      alert('Failed to fetch options.');
    }
  };

  const handleFileChange = (event) => {
    setImageFile(event.target.files[0]);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('file', imageFile);

    const selectedColorObj = colorOptions.find(color => color.id === parseInt(selectedColor));
    const selectedSizeObj = sizeOptions.find(size => size.id === parseInt(selectedSize));
    const selectedGenderObj = genderOptions.find(gender => gender.id === parseInt(selectedGender));
    const selectedCategoryObj = categoryOptions.find(category => category.id === parseInt(selectedCategory));

    const productData = {
      name,
      imageURL: '',
      description,
      price,
      color: selectedColorObj,
      size: selectedSizeObj,
      gender: selectedGenderObj,
      category: selectedCategoryObj,
    };

    try {
      const createProductResponse = await axios.post('http://localhost:8080/products', productData);
      const createdProduct = createProductResponse.data;
      const productId = createdProduct.id;

      const uploadImageResponse = await axios.post(`http://localhost:8080/products/upload/${productId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      alert('Product successfully added.');

      // Reset form
      setName('');
      setImageFile(null);
      setDescription('');
      setSelectedSize('');
      setPrice(0);
      setSelectedColor('');
      setSelectedGender('');
      setSelectedCategory('');
    } catch (error) {
      console.error(error);
      alert('Failed to add product.');
    }
  };

  return (
    <div className="form-container">
      <h2>Add Product</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>Image:</label>
          <input type="file" onChange={handleFileChange} />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <label>Size:</label>
          <select value={selectedSize} onChange={(e) => setSelectedSize(e.target.value)}>
            <option value="">Select Size</option>
            {sizeOptions.map(size => (
              <option key={size.id} value={size.id}>{size.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Price:</label>
          <input type="number" value={price} onChange={(e) => setPrice(parseInt(e.target.value))} />
        </div>
        <div>
          <label>Color:</label>
          <select value={selectedColor} onChange={(e) => setSelectedColor(e.target.value)}>
            <option value="">Select Color</option>
            {colorOptions.map(color => (
              <option key={color.id} value={color.id}>{color.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Gender:</label>
          <select value={selectedGender} onChange={(e) => setSelectedGender(e.target.value)}>
            <option value="">Select Gender</option>
            {genderOptions.map(gender => (
              <option key={gender.id} value={gender.id}>{gender.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Category:</label>
          <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
            <option value="">Select Category</option>
            {categoryOptions.map(category => (
              <option key={category.id} value={category.id}>{category.name}</option>
            ))}
          </select>
        </div>
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default AddProduct;
