import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";

class Profile extends Component {
  state = {
    redirect: null,
    userReady: false,
    currentUser: { username: "" }
  };

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) {
      this.setState({ redirect: "/home" });
    } else {
      this.setState({ currentUser: currentUser, userReady: true });
    }
  }

  render() {
    const { redirect, userReady, currentUser } = this.state;

    if (redirect) {
      return <Navigate to={redirect} />;
    }

    return (
      <div
        style={{
          maxWidth: "800px",
          margin: "auto",
          padding: "20px",
          boxShadow: "0 0 10px rgba(0,0,0,0.1)"
        }}
      >
        {userReady && (
          <div>
            <header
              style={{
                padding: "2rem 1rem",
                marginBottom: "2rem",
                backgroundColor: "#f9f9f9",
                borderRadius: "0.3rem",
                fontSize: "1.5rem"
              }}
            >
              <h3 style={{ color: "#333" }}>Bok, {currentUser.username}!</h3>
            </header>
            <p style={{ fontSize: "1.1rem" }}>
              Dobrodošli u <b style={{ color: "#007bff" }}>Pien!</b>
            </p>
            <div style={{ fontSize: "1.2rem", lineHeight: "1.6" }}>
              <ul>
                <li>Pregledajte najnovije proizvode</li>
                <li>Pregledajte prethodne narudžbe</li>
                <li>Istražite nešto novo</li>
                <li>Uživajte u online kupnji!</li>
              </ul>
            </div>
          </div>
        )}
      </div>
    );
  }
}

export default Profile;
