import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './ProductList.css';
import authHeader from './services/auth-header';

function ProductList() {
    const [products, setProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [productCategories, setProductCategories] = useState([]);
    const [orderId, setOrderId] = useState(localStorage.getItem('orderId'));

    // Additional state for modal and editing
    const [isModalOpen, setModalOpen] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [formFields, setFormFields] = useState({
        name: '',
        description: '',
        price: ''
        // Add other product fields as needed...
    });

    // Existing useEffect and fetch functions ...

    // Functions for modal operations
    const openModal = (product) => {
        setEditingProduct(product);
        setFormFields({
            name: product.name,
            description: product.description,
            price: product.price
            // Set other product fields as needed...
        });
        setModalOpen(true);
    };

    const closeModal = () => {
        setModalOpen(false);
        setEditingProduct(null);
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormFields(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleEditSubmit = async (event) => {
        event.preventDefault();
        try {
            await axios.put(`http://localhost:8080/products/${editingProduct.id}`, formFields, {
                headers: authHeader(),
            });
            fetchProducts(); // Refresh after edit
            closeModal();
        } catch (error) {
            console.error('Error updating the product:', error);
        }
    };

    return (
        <div>
            <h2>Popis proizvoda</h2>
            <div className="category-list">
                <button onClick={() => setSelectedCategory('')}>Svi proizvodi</button>
                {productCategories.map((category) => (
                    <button key={category.id} onClick={() => setSelectedCategory(category.name)}>
                        {category.name}
                    </button>
                ))}
            </div>
            
            <div className="product-list">
                {products.map((product) => (
                    <div key={product.id} className="product-item">
                        <h3>{product.name}</h3>
                        <img src={`http://localhost:8080/products/images/${product.imageURL}`} 
                             alt="Slika proizvoda"
                             className="product-image"/>
                        <p>{product.description}</p>
                        <p>Veličina: {product.size?.name}</p>
                        <p>Cijena: {product.price}€</p>
                        <p>Boja: {product.color?.name}</p>
                        <p>Spol: {product.gender?.name}</p>
                        <button onClick={() => addToOrder(product.id, product.price)}>Dodaj u narudžbu</button>
                        <button onClick={() => deleteProduct(product.id)}>Obriši</button>
                        <button onClick={() => openModal(product)}>Edit</button>
                    </div>
                ))}
            </div>
            <Link to="/cart">Prikaži košaricu</Link>

            {/* Modal for editing */}
            {isModalOpen && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>Edit Product</h2>
                        <form onSubmit={handleEditSubmit}>
                            <label>Name:</label>
                            <input type="text" name="name" value={formFields.name} onChange={handleInputChange} />
                            <label>Description:</label>
                            <textarea name="description" value={formFields.description} onChange={handleInputChange} />
                            <label>Price:</label>
                            <input type="number" name="price" value={formFields.price} onChange={handleInputChange} />
                            {/* Add other fields as needed */}
                            <button type="submit">Update</button>
                            <button onClick={closeModal}>Cancel</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ProductList;
