import React, { useState } from 'react';
import axios from 'axios';

const handleSubmit = async (event) => {
  event.preventDefault();

  const email = event.target.elements.email.value;
  const password = event.target.elements.password.value;

  try {
    const response = await axios.post('http://localhost:8080/users/login', { email, password });
    console.log(response.data);
    // Ovdje možete izvršiti dodatne radnje nakon uspješne prijave, kao što je spremanje tokena u sessionStorage ili redirekcija na drugu stranicu.
  } catch (error) {
    console.error(error);
    // Ovdje možete obraditi grešku pri prijavi, kao što je prikazivanje poruke korisniku.
  }
}

<form onSubmit={handleSubmit}>
  <input type="text" name="username" placeholder="Korisničko ime" />
  <input type="password" name="password" placeholder="Lozinka" />
  <button type="submit">Prijavi se</button>
</form>

export default LoginForm;
