import './App.css';
import Home from './Home';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'
import LoginForm from './loginForm';
import RegistrationForm from './registrationForm';
import AddProduct from './AddProduct';
import ProductList from './ProductList';
import CartPage from './CartPage'; // Nova import linija
import AddProductCategory from './AddProductCategory';
import AddSize from './AddSize';
import AddColor from './AddColor';
import AddGender from './AddGender';
import { useState } from 'react'; // Nova import linija

function App() {
  const [orderItems, setOrderItems] = useState([]); // Novo stanje za stavke narudžbe

  const handleAddToOrder = (item) => { // Funkcija za dodavanje stavki u narudžbu
    setOrderItems([...orderItems, item]);
  };

  return (
    <Routes>
      <Route path='/' element={<Home/>} />
      <Route path='/login' element={<LoginForm/>} />
      <Route path='/home' element={<Home/>} />
      <Route path='/register' element={<RegistrationForm/>} />
      <Route path="/add-product" element={<AddProduct />} />
      <Route path="/product-list" element={<ProductList onAddToOrder={handleAddToOrder} />} /> {/* Proslijedimo funkciju dodavanja u narudžbu u komponentu ProductList */}
      <Route path="/cart" element={<CartPage orderItems={orderItems} />} /> {/* Nova ruta za prikaz košarice */}
      <Route path="/add-productcategory" element={<AddProductCategory />} />
      <Route path="/add-size" element={<AddSize />} />
      <Route path="/add-color" element={<AddColor />} />
      <Route path="/add-gender" element={<AddGender />} />
    </Routes>
  );
}

export default App;
