import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AddProduct() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    imageURL: '',
    description: '',
    price: 0,
    color: null,
    size: null,
    gender: null,
    category: null,
  });

  useEffect(() => {
    fetchAllProducts();
  }, []);

  const fetchAllProducts = async () => {
    try {
      const response = await axios.get('http://localhost:8080/products/all');
      setProducts(response.data);
    } catch (error) {
      console.error(error);
      alert('Failed to fetch products.');
    }
  };

  const addProduct = async () => {
    try {
      const response = await axios.post('http://localhost:8080/products/add', newProduct);
      const createdProduct = response.data;
      setProducts([...products, createdProduct]);
      setNewProduct({
        name: '',
        imageURL: '',
        description: '',
        price: 0,
        color: null,
        size: null,
        gender: null,
        category: null,
      });
      alert('Product added successfully.');
    } catch (error) {
      console.error(error);
      alert('Failed to add product.');
    }
  };

  const handleChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <h2>Product List</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            {product.name} - {product.price}
          </li>
        ))}
      </ul>
      <h2>Add Product</h2>
      <div>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" value={newProduct.name} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="imageURL">Image URL:</label>
        <input type="text" id="imageURL" name="imageURL" value={newProduct.imageURL} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="description">Description:</label>
        <input type="text" id="description" name="description" value={newProduct.description} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="price">Price:</label>
        <input type="number" id="price" name="price" value={newProduct.price} onChange={handleChange} />
      </div>
      {/* Add input fields for color, size, gender, category */}
      <button onClick={addProduct}>Add Product</button>
    </div>
  );
}

export default AddProduct;
