import React, { useState } from 'react';
import axios from 'axios';
import './loginForm.css';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/users/login', {
        email,
        password,
      });
      console.log('Odgovor:', response.data);

      // Spremanje informacija o prijavljenom korisniku
      localStorage.setItem('token', response.data.token);
      // Možete spremiti druge informacije o korisniku, kao što je ID ili uloga

      // Preusmjeravanje na početnu stranicu
      window.location.href = '/home';
    } catch (error) {
      setErrorMessage(error.response.data);
      console.error(error);
    }
  };

  return (
    <div className="login-container">
      <h1 className="loginfont">Login</h1>
      <ul>
        {errorMessage && <p>{errorMessage}</p>}
        <li>
          E-mail:
          <input
            className="login-input"
            type="text"
            name="email"
            placeholder="Email"
            value={email}
            onChange={handleEmailChange}
          />
        </li>
        <li>
          Password:
          <input
            className="login-input"
            type="password"
            name="password"
            placeholder="Password"
            value={password}
            onChange={handlePasswordChange}
          />
        </li>
        <button className="login-button" onClick={handleLogin}>
          Login
        </button>
      </ul>
    </div>
  );
};

export default LoginForm;
