import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";
import "./Profile.css"; 

class Profile extends Component {
  state = {
    redirect: null,
    userReady: false,
    currentUser: { username: "" }
  };

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) {
      this.setState({ redirect: "/home" });
    } else {
      this.setState({ currentUser: currentUser, userReady: true });
    }
  }

  render() {
    const { redirect, userReady, currentUser } = this.state;

    if (redirect) {
      return <Navigate to={redirect} />;
    }

    return (
      <div className="profile-container">
        {userReady && (
          <div className="profile-jumbotron fade-in">
            <h3 className="profile-header">Heej, {currentUser.username}!</h3>
            <p className="profile-lead">
              Dobrodošli u <b>Pien!</b>
            </p>
            <ul className="profile-list">
              <li>Nadamo se da ćete uživati u online kupnji.</li>
              <li>Vjerujemo da možete pronaći nešto za sebe. :)</li>
  
            </ul>
          </div>
        )}
      </div>
    );
  }
}

export default Profile;
