import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProductList.css';
import authenticationHeader from './services/authentication-header';

function ProductList() {
    const [products, setProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [productCategories, setProductCategories] = useState([]);
    const [colors, setColors] = useState([]);
    const [sizes, setSizes] = useState([]);
    const [genders, setGenders] = useState([]);
    const [orderId, setOrderId] = useState(localStorage.getItem('orderId'));
    const [isModalOpen, setModalStatus] = useState(false);
    const [editingProduct, setProductForEditing] = useState(null);
    const [formFields, setFormInputFields] = useState({
        name: '',
        description: '',
        price: '',
        color: '',
        size: '',
        gender: '',
        imageURL: ''
    });

    useEffect(() => {
        retrieveProducts();
        retrieveProductCategories();
        retrieveColors();
        retrieveSizes();
        retrieveGenders();
    }, []);

    useEffect(() => {
        if (selectedCategory) {
            retrieveProductsByCategory(selectedCategory);
        } else {
            retrieveProducts();
        }
    }, [selectedCategory]);

    const retrieveProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8080/products', { headers: authenticationHeader() });
            setProducts(response.data);
        } catch (error) {
            console.error("Error retrieving products:", error);
        }
    };

    const retrieveProductsByCategory = async (category) => {
        try {
            const response = await axios.get(`http://localhost:8080/products?category=${category}`, {
                headers: authenticationHeader(),
            });
            setProducts(response.data);
        } catch (error) {
            console.error("Error retrieving products by category:", error);
        }
    };

    const retrieveProductCategories = async () => {
        try {
            const response = await axios.get('http://localhost:8080/productsCategory', {
                headers: authenticationHeader(),
            });
            setProductCategories(response.data);
        } catch (error) {
            console.error("Error retrieving product categories:", error);
        }
    };

    const retrieveColors = async () => {
        try {
            const response = await axios.get('http://localhost:8080/colors', {
                headers: authenticationHeader(),
            });
            setColors(response.data);
        } catch (error) {
            console.error("Error retrieving colors:", error);
        }
    };

    const retrieveSizes = async () => {
        try {
            const response = await axios.get('http://localhost:8080/sizes', {
                headers: authenticationHeader(),
            });
            setSizes(response.data);
        } catch (error) {
            console.error("Error retrieving sizes:", error);
        }
    };

    const retrieveGenders = async () => {
        try {
            const response = await axios.get('http://localhost:8080/genders', {
                headers: authenticationHeader(),
            });
            setGenders(response.data);
        } catch (error) {
            console.error("Error retrieving genders:", error);
        }
    };

    const createOrder = async () => {
        try {
            const response = await axios.post('http://localhost:8080/orders', {}, { headers: authenticationHeader() });
            setOrderId(response.data.id);
        } catch (error) {
            console.error("Error creating order:", error);
        }
    };

    const addToOrder = async (productId, price, quantity = 1) => {
        let currentOrderId = orderId;
        if (!currentOrderId) {
            currentOrderId = await createOrder();
        }
        const data = {
            quantity: quantity,
            price: price,
        };
        try {
            await axios.post(`http://localhost:8080/orderDetails?orderId=${currentOrderId}&productId=${productId}`, data, {
                headers: authenticationHeader(),
            });
            alert(`Product with ID ${productId} added to order.`);
        } catch (error) {
            console.error("Error adding to order:", error);
        }
    };

    const deleteProduct = async (productId) => {
        try {
            await axios.delete(`http://localhost:8080/products/${productId}`, {
                headers: authenticationHeader(),
            });
            alert('Product successfully deleted.');
            retrieveProducts();
        } catch (error) {
            console.error('Error deleting product:', error);
        }
    };

    const openModal = (product) => {
        setProductForEditing(product);
        setFormInputFields({
            name: product.name,
            description: product.description,
            price: product.price,
            color: product.color?.name,
            size: product.size?.name,
            gender: product.gender?.name,
            imageURL: product.imageURL
        });
        setModalStatus(true);
    };

    const closeModal = () => {
        setModalStatus(false);
        setProductForEditing(null);
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormInputFields(previousState => ({
            ...previousState,
            [name]: value
        }));
    };

    const handleEditSubmit = async (event) => {
        event.preventDefault();
        const updatedProduct = {
            id: editingProduct.id,
            name: formFields.name,
            description: formFields.description,
            price: parseFloat(formFields.price),
            color: colors.find(color => color.name === formFields.color),
            size: sizes.find(size => size.name === formFields.size),
            gender: genders.find(gender => gender.name === formFields.gender),
            imageURL: formFields.imageURL
        };
        
        try {
            await axios.put(`http://localhost:8080/products/${updatedProduct.id}`, updatedProduct, {
                headers: authenticationHeader(),
            });
            retrieveProducts();
            closeModal();
        } catch (error) {
            console.error('Error updating product:', error);
        }
    };

    return (
        <div>
            {/* Your code for displaying products, dropdowns, etc. goes here. */}
            {isModalOpen && 
                <div className="edit-modal-overlay" onClick={closeModal}>
                    <div className="edit-modal" onClick={event => event.stopPropagation()}>
                        <form onSubmit={handleEditSubmit}>
                            {/* Your code for the form within the modal goes here. */}
                        </form>
                    </div>
                </div>
            }
        </div>
    );
}

export default ProductList;
