import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from './services/auth-header';
import './AllOrders.css';

function AllOrders() {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [orders, setOrders] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [topSpenders, setTopSpenders] = useState([]);
    const [topBuyers, setTopBuyers] = useState([]);

    useEffect(() => {
        fetchAllUsers();
        fetchTopSpenders();
        fetchTopBuyers();
    }, []);

    const fetchAllUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/users', {
                headers: authHeader(),
            });
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const fetchOrdersByUser = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8080/orderDetails/details/user/${userId}`, {
                headers: authHeader(),
            });
            setOrders(response.data);

            const total = response.data.reduce((acc, orderDetail) => acc + (orderDetail.quantity * orderDetail.product.price), 0);
            setTotalPrice(total);
        } catch (error) {
            console.error('Error fetching user orders:', error);
        }
    };

    const fetchTopSpenders = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/topSpenders', {
                headers: authHeader(),
            });
            setTopSpenders(response.data);
        } catch (error) {
            console.error('Error fetching top spenders:', error);
        }
    };

    const fetchTopBuyers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/top-selling-products', {
                headers: authHeader(),
            });
            setTopBuyers(response.data);
        } catch (error) {
            console.error('Error fetching top buyers:', error);
        }
    };

    const handleUserClick = (user) => {
        setSelectedUser(user);
        setOrders([]);
        setTotalPrice(0);
        
        fetchOrdersByUser(user.id);
    };

    return (
        <div className="orders-container">
            <h2>Users</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Username</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>
                                <a href="#" onClick={() => handleUserClick(user)}>{user.username}</a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Top Spenders</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Username</th>
                        {/* Dodajte druge relevantne zaglavlja ovisno o tome što vraća vaš API */}
                    </tr>
                </thead>
                <tbody>
                    {topSpenders.map((user) => (
                        <tr key={user.id}>
                            <td>{user.username}</td>
                            {/* Dodajte druge relevantne podatke ovisno o tome što vraća vaš API */}
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Top Buyers</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Username</th>
                        {/* Dodajte druge relevantne zaglavlja ovisno o tome što vraća vaš API */}
                    </tr>
                </thead>
                <tbody>
                    {topBuyers.map((user) => (
                        <tr key={user.id}>
                            <td>{user.username}</td>
                            {/* Dodajte druge relevantne podatke ovisno o tome što vraća vaš API */}
                        </tr>
                    ))}
                </tbody>
            </table>

            {selectedUser && (
                <div>
                    <h3>Orders of {selectedUser.username}</h3>
                    <table className="orders-table">
                        <thead>
                            <tr>
                                <th>Product Image</th>
                                <th>Product Name</th>
                                <th>Quantity</th>
                                <th>Price</th>
                                <th>Date of Order</th>
                            </tr>
                        </thead>
                        <tbody>
                            {orders.map((orderDetail) => (
                                <tr key={orderDetail.id}>
                                    <td><img src={`http://localhost:8080/products/images/${orderDetail.product.imageURL}`} alt={orderDetail.product.name} width="100" /></td>
                                    <td>{orderDetail.product.name}</td>
                                    <td>{orderDetail.quantity}</td>
                                    <td>{orderDetail.product.price} €</td>
                                    <td>{new Date(orderDetail.order.orderTime).toLocaleString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <strong><p>Total Price: {totalPrice} €</p></strong>
                </div>
            )}
        </div>
    );
}

export default AllOrders;
