import React, { useState } from 'react';
import axios from 'axios';
import './LoginPage.css';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const loginUser =(email, password) => {
    
    const user={
      email: email,
      password: password,
    } 
    try {
      const response = axios.post('http://localhost:8080/users/login', user)
      console.log('Odgovor:', response)
      window.location.href = '/home';
    } catch (error) {
      console.error('Greška:', error.response.data);
    }
  
  };
 

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = () => {
    loginUser(email, password);
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <ul>
        <li>E-mail:<input type="text" name='email' placeholder="Email" value={email} onChange={handleEmailChange} /></li>
        <li>Password:<input type="password" name='password' placeholder="Password" value={password} onChange={handlePasswordChange} /></li>
        <button onClick={handleLogin}>Login</button>
      </ul>
    </div>
  );
};

export default LoginForm;
