import React, { useState, useEffect } from "react";
import axios from 'axios';
import authHeader from '../services/auth-header';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
    const [cart, setCart] = useState([]);
    const user = JSON.parse(localStorage.getItem('user'));
    const navigate = useNavigate();

    useEffect(() => {
        const fetchCart = async () => {
            if (user && user.id) {
                const response = await axios.get(`http://localhost:8080/orders/${user.id}`, { headers: authHeader() });
                if (response.data) {
                    setCart(response.data);
                }
            }
        };

        fetchCart();
    }, [user]);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/orderDetails/${id}`, { headers: authHeader() });
            setCart(cart.filter(item => item.id !== id));
        } catch (error) {
            console.error('Error deleting the item', error);
        }
    };

    const updateQuantity = async (id, newQuantity) => {
        if (newQuantity < 1) return; // Optionally prevent setting quantity below 1

        try {
            await axios.put(`http://localhost:8080/orders/${user.id}/${id}`, { quantity: newQuantity }, { headers: authHeader() });
            setCart(cart.map(item => item.id === id ? { ...item, quantity: newQuantity } : item));
        } catch (error) {
            console.error('Error updating the quantity:', error);
        }
    };

    const completeOrder = () => {
        navigate('/order-success');
    };

    if (!cart || cart.length === 0) {
        return <p>You have no items in your cart.</p>;
    }

    const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

    return (
        <div className="container mt-5">
            <h2>Your Cart</h2>
            <table className="table mt-4">
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Description</th>
                        <th>Quantity</th>
                        <th>Price per unit</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {cart.map(item => (
                        <tr key={item.id}>
                            <td>{item.product.name}</td>
                            <td>{item.product.description}</td>
                            <td>
                                <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
                                {item.quantity}
                                <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
                            </td>
                            <td>{item.price}</td>
                            <td>
                                <button onClick={() => handleDelete(item.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <h3 className="mt-4">Total: {total.toFixed(2)}</h3>
            <button onClick={completeOrder} className="btn btn-primary">Complete Order</button>
        </div>
    );
};

export default Cart;
