import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from './services/auth-header';

function AllOrders() {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
  
    useEffect(() => {
        fetchAllUsers();
    }, []);
  
    const fetchAllUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/users', {
                headers: authHeader(),
            });
            setUsers(response.data);
        } catch (error) {
            console.error('Došlo je do pogreške prilikom dohvaćanja korisnika:', error);
            setError(error.message);
        }
    };
  
    const fetchOrdersByUser = async (userId) => {
        try {
           const response = await axios.get(`http://localhost:8080/orderDetails/details/user/${userId}`, {
          headers: authHeader(),
          });
            setOrders(response.data);
        } catch (error) {
            console.error('Došlo je do pogreške prilikom dohvaćanja narudžbi korisnika:', error);
            setError(error.message);
        }
    };
  
    const handleUserClick = (user) => {
        setSelectedUser(user);
        fetchOrdersByUser(user.id);
    };
  
    return (
        <div>
            <h2>Popis korisnika</h2>
            <div className="user-list">
                {users.map((user) => (
                    <div key={user.id} className="user-item">
                        <a href="#" onClick={() => handleUserClick(user)}>{user.username}</a>
                    </div>
                ))}
            </div>
            {selectedUser && (
                <div>
                    <h3>Narudžbe korisnika {selectedUser.username}</h3>
                    <div className="order-list">
                        {orders.length > 0 ? (
    orders.map((orderDetail) => (
        <div key={orderDetail.id} className="order-item">
            <h4>Narudžba ID: {orderDetail.order.id}</h4>
            <img src={orderDetail.product.productImage} alt={orderDetail.product.productName} width="100" />
            <p>Proizvod: {orderDetail.product.name}</p>
            <p>Količina: {orderDetail.quantity}</p>
            <p>Cijena: {orderDetail.product.price}</p>
        </div>
    ))
) : (
    <p>Nema još narudžbi.</p>
)}
                    </div>
                </div>
            )}
        </div>
    );
}
  
export default AllOrders;
