import React, { useState } from 'react';
import axios from 'axios';
import './AddProduct.css'; // Uvoz CSS datoteke

function AddProduct() {
  const [name, setName] = useState('');
  const [imageURL, setImageURL] = useState('');
  const [description, setDescription] = useState('');
  const [sizeId, setSizeId] = useState(0);
  const [price, setPrice] = useState(0);
  const [colorId, setColorId] = useState(0);
  const [genderId, setGenderId] = useState(0);
  const [categoryId, setCategoryId] = useState(0);

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    const sizeData = {
      name,
     
    };

    axios
      .post('http://localhost:8080/sizes', sizeData)
      .then((response) => {
        console.log(response.data);
        alert('Proizvod je uspješno dodan.');
        // Resetirajte formu
        setName('');
      })
      .catch((error) => {
        console.error(error);
        alert('Došlo je do pogreške prilikom dodavanja proizvoda.');
      });
  };

  return (
    <div className="form-container"> {/* Dodajte klasu na vanjski div */}
      <h2>Add size</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label>Naziv:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <button type="submit">Dodaj</button>
      </form>
    </div>
  );
}

export default AddProduct;
