import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";
import logo from '../assets/logo.png';  // Pretpostavimo da je logo.png smješten u 'assets' mapi

export default class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      redirect: null, 
      userReady: false,
      currentUser: { username: "" }
    };
  }

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) this.setState({ redirect: "/home" });
    this.setState({ currentUser: currentUser, userReady: true })
  }

  render() {
    if (this.state.redirect) {
      return <Navigate to={this.state.redirect} />
    }

    const { currentUser } = this.state;

    return (
      <div className="container">
        <img src={logo} alt="Brand Logo" style={{width: '150px', marginBottom: '20px'}} />
        {this.state.userReady ? (
          <div>
            <header className="jumbotron">
              <h3>
                Bok, {currentUser.username} !
              </h3>
            </header>
            <p>Dobrodošli u naš online web shop!</p>
            <p>Ovdje možete pregledati svoje podatke, pratiti narudžbe i upravljati postavkama profila.</p>
            <ul>
              <li>Pregledajte najnovije proizvode</li>
              <li>Pratite status svojih narudžbi</li>
              <li>Pregledajte i ažurirajte svoje postavke</li>
              <li>Kontaktirajte korisničku podršku</li>
            </ul>
          </div>
        ) : null}
      </div>
    );
  }
}
