import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";

class Profile extends Component {
  state = {
    redirect: null,
    userReady: false,
    currentUser: { username: "" }
  };

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) {
      this.setState({ redirect: "/home" });
    } else {
      this.setState({ currentUser: currentUser, userReady: true });
    }
  }

  render() {
    const { redirect, userReady, currentUser } = this.state;

    if (redirect) {
      return <Navigate to={redirect} />;
    }

    return (
      <div className="container my-5">
        {userReady && (
          <div className="jumbotron text-center">
            <h3 className="display-4">Bok, {currentUser.username}!</h3>
            <p className="lead">
              Dobrodošli u <b>Pien!</b>
            </p>
            <ul className="list-unstyled">
              <li>Pregledajte najnovije proizvode</li>
              <li>Pregledajte prethodne narudžbe</li>
              <li>Istražite nešto novo</li>
              <li>Uživajte u online kupnji!</li>
            </ul>
          </div>
        )}
      </div>
    );
  }
}

export default Profile;
