import React, { useState } from 'react';
import axios from 'axios';
import './loginForm.css'


const logoutUser = () => {
  // Implementirajte kod za odjavu korisnika, kao što je brisanje tokena sesije ili slično
  
  // Nakon odjave, preusmjerite korisnika na stranicu za prijavu
  window.location.href = '/login';
};
const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const loginUser = async (email, password) => {
    
    const user={
      email: email,
      password: password,
    } 
    try {
      const response = await axios.post('http://localhost:8080/users/login', user)
      console.log('Odgovor:', response)
      window.location.href = '/home';
    } catch (error) {
      console.error('Greška:', error.response.data);
      window.location.href = '/login';
    }
  }

 

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = () => {
    loginUser(email, password);
  };

  return (
    <div className="login-container">
      <h1 className='loginfont'>Login</h1>
      <ul>
      
        <li>E-mail:<input className="login-input" type="text" name='email' placeholder="Email" value={email} onChange={handleEmailChange} /></li>
        <li>Password:<input className="login-input"  type="password" name='password' placeholder="Password" value={password} onChange={handlePasswordChange} /></li>
        <button className="login-button" onClick={handleLogin}>Login</button>
      </ul>
    </div>
  );
};

export default LoginForm;
