import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function CartPage() {
  const [order, setOrder] = useState(null);
  const [orderDetails, setOrderDetails] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Replace with actual user order ID
    const orderId = 1;

    const fetchOrderAndDetails = async () => {
      try {
        // Fetch UserOrder
        const orderResponse = await axios.get(`http://localhost:8080/orders/${orderId}`);
        setOrder(orderResponse.data);

        // Fetch OrderDetails
        const detailsResponse = await axios.get('http://localhost:8080/orderDetails');
        const fetchedOrderDetails = detailsResponse.data;

        // Store order details
        setOrderDetails(fetchedOrderDetails);

        // Fetch products
        const productRequests = fetchedOrderDetails.map(detail =>
          axios.get(`http://localhost:8080/products/${detail.productId}`),
        );
        const productResponses = await Promise.all(productRequests);
        setProducts(productResponses.map(res => res.data));

      } catch (error) {
        console.error(error);
        alert('Došlo je do pogreške prilikom dohvaćanja narudžbe i detalja.');
      }
    };

    fetchOrderAndDetails();
  }, []);

  return (
    <div>
      <h3>Košarica</h3>
      {products.map((product, index) => (
        <div key={index} className="cart-item">
          <p>Proizvod ID: {product.id}</p>
          <p>Ime proizvoda: {product.name}</p>
          <p>Cijena: {product.price}</p>
          {/* Add any other necessary fields */}
        </div>
      ))}
      <Link to="/">Natrag na popis proizvoda</Link>
    </div>
  );
}

export default CartPage;
