import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from './services/auth-header';

function AllOrders() {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchAllUsers();
    }, []);

    const fetchAllUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/users', {
                headers: authHeader(),
            });
            setUsers(response.data);
        } catch (error) {
            console.error('Došlo je do pogreške prilikom dohvaćanja korisnika:', error);
            setError(error.message);
        }
    };

    const fetchOrdersByUser = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8080/orderDetails/details/user/${userId}`, {
                headers: authHeader(),
            });
            setOrders(response.data);
        } catch (error) {
            console.error('Došlo je do pogreške prilikom dohvaćanja narudžbi korisnika:', error);
            setError(error.message);
        }
    };

    const handleUserClick = (user) => {
        setSelectedUser(user);
        setOrders([]);
        setError(null);
        fetchOrdersByUser(user.id);
    };

    return (
        <div>
            <h2>Popis korisnika</h2>
            <table className="user-table">
                <thead>
                    <tr>
                        <th>Username</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>
                                <a href="#" onClick={() => handleUserClick(user)}>{user.username}</a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {selectedUser && (
                <div>
                    <h3>Narudžbe korisnika {selectedUser.username}</h3>
                    <table className="order-table">
                        <thead>
                            <tr>
                                <th>Slika</th>
                                <th>Proizvod</th>
                                <th>Količina</th>
                                <th>Cijena</th>
                            </tr>
                        </thead>
                        <tbody>
                            {orders.length > 0 ? (
                                orders.map((orderDetail) => (
                                    <tr key={orderDetail.id}>
                                        <td>
                                            <img src={`http://localhost:8080/products/images/${orderDetail.product.imageURL}`} alt={orderDetail.product.name} width="100" />
                                        </td>
                                        <td>{orderDetail.product.name}</td>
                                        <td>{orderDetail.quantity}</td>
                                        <td>{orderDetail.product.price}</td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="4">Nema još narudžbi.</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                    {orders.length > 0 && (
                        <div className="total-price">Ukupna cijena: {orders.reduce((acc, orderDetail) => acc + (orderDetail.product.price * orderDetail.quantity), 0)} kn</div>
                    )}
                </div>
            )}
        </div>
    );
}

export default AllOrders;
