import React, { useState } from 'react';
import axios from 'axios';

function AddProduct() {
  const [name, setName] = useState('');
  const [imageURL, setImageURL] = useState('');
  const [description, setDescription] = useState('');
  const [size, setSize] = useState('');
  const [price, setPrice] = useState(0);
  const [colorId, setColorId] = useState(0);
  const [genderId, setGenderId] = useState(0);
  const [categoryId, setCategoryId] = useState(0);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    const productData = {
      name,
      imageURL,
      description,
      size,
      price,
      colorId,
      genderId,
      categoryId
    };

    axios.post('http://localhost:8080/products/add', productData)
      .then(response => {
        console.log(response.data);
        alert('Proizvod je uspješno dodan.');
        // Resetirajte formu
        setName('');
        setImageURL('');
        setDescription('');
        setSize('');
        setPrice(0);
        setColorId(0);
        setGenderId(0);
        setCategoryId(0);
      })
      .catch(error => {
        console.error(error);
        alert('Došlo je do pogreške prilikom dodavanja proizvoda.');
      });
  };

  return (
    <div>
      <h2>Dodaj proizvod</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label>Naziv:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>URL slike:</label>
          <input type="text" value={imageURL} onChange={(e) => setImageURL(e.target.value)} />
        </div>
        <div>
          <label>Opis:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <label>Veličina:</label>
          <input type="text" value={size} onChange={(e) => setSize(e.target.value)} />
        </div>
        <div>
          <label>Cijena:</label>
          <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} />
        </div>
        <div>
          <label>ID boje:</label>
          <input type="number" value={colorId} onChange={(e) => setColorId(e.target.value)} />
        </div>
        <div>
          <label>ID spola:</label>
          <input type="number" value={genderId} onChange={(e) => setGenderId(e.target.value)} />
        </div>
        <div>
          <label>ID kategorije:</label>
          <input type="number" value={categoryId} onChange={(e) => setCategoryId(e.target.value)} />
        </div>
        <button type="submit">Dodaj</button>
      </form>
    </div>
  );
}

export default AddProduct;
