import React, { useState, useEffect } from 'react';

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/products')
      .then(response => response.json())
      .then(data => setProducts(data));
  }, []);

  // Kreirajte objekt s praznim poljima za svaku kategoriju
  const categorizedProducts = products.reduce((categories, product) => {
    const category = product.category;
    if (!categories[category]) {
      categories[category] = [];
    }
    categories[category].push(product);
    return categories;
  }, {});

  return (
    <div>
      <h1>Product List</h1>
      {/* Iterirajte kroz kategorije */}
      {Object.entries(categorizedProducts).map(([category, productList]) => (
        <div key={category}>
          <h2>{category}</h2>
          <ul>
            {/* Iterirajte kroz proizvode unutar kategorije */}
            {productList.map(product => (
              <li key={product.id}>{product.name}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
