import React, { useEffect, useState } from 'react';
import axios from 'axios';
import authHeader from '../services/auth-header';
import { useParams } from 'react-router-dom';

const ProductDetails = ({ match }) => {
  const [product, setProduct] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    const fetchProduct = async () => {
      const response = await axios.get(`http://localhost:8080/products/${match.params.id}`, {
        headers: authHeader(),
      });
      setProduct(response.data);
    };
    fetchProduct();
  }, [match.params.id]);

  const addToCart = () => {
    // Ovdje dodajte logiku za dodavanje u košaricu
  };

  return (
    <div>
      {product ? (
        <div>
          <h2>{product.name}</h2>
          <img 
            src={`http://localhost:8080/products/images/${product.imageURL}`} 
            alt={product.name} 
          />
          <p>{product.description}</p>
          <p>Cijena: {product.price} USD</p>
          <button onClick={addToCart}>Dodaj u košaricu</button>
        </div>
      ) : (
        <p>Učitavanje proizvoda...</p>
      )}
    </div>
  );
};

export default ProductDetails;
