import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from '../services/auth-header';
import '../AllOrders.css';

function AllOrders() {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [orders, setOrders] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [topSpenders, setTopSpenders] = useState([]);
    const [topBuyers, setTopBuyers] = useState([]);

    useEffect(() => {
        fetchAllUsers();
        fetchTopSpenders();
        fetchTopBuyers();
    }, []);

    useEffect(() => {
        fetchAllUsers().then(users => {
            const fetchTotalSpentForUser = userId => {
                return fetchOrdersByUser(userId)
                    .then(orders => {
                        return orders.reduce((acc, order) => acc + (order.quantity * order.product.price), 0);
                    });
            };

            const userPromises = users.map(async user => {
                const totalSpent = await fetchTotalSpentForUser(user.id);
                return { ...user, totalSpent };
            });

            Promise.all(userPromises)
                .then(usersWithTotalSpent => {
                    usersWithTotalSpent.sort((a, b) => b.totalSpent - a.totalSpent);
                    setUsers(usersWithTotalSpent);
                });
        });
    }, []);

    const fetchAllUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/users', {
                headers: authHeader(),
            });
            setUsers(response.data);
            return response.data;
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const fetchOrdersByUser = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8080/orderDetails/details/user/${userId}`, {
                headers: authHeader(),
            });
            if (userId === selectedUser.id) {
                setOrders(response.data);
                const total = response.data.reduce((acc, orderDetail) => acc + (orderDetail.quantity * orderDetail.product.price), 0);
                setTotalPrice(total);
            }
            return response.data;
        } catch (error) {
            console.error('Error fetching user orders:', error);
        }
    };

    const fetchTopSpenders = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/top-spending-users', {
                headers: authHeader(),
            });
            setTopSpenders(response.data);
        } catch (error) {
            console.error('Error fetching top spenders:', error);
        }
    };

    const fetchTopBuyers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/top-selling-products', {
                headers: authHeader(),
            });
            setTopBuyers(response.data);
        } catch (error) {
            console.error('Error fetching top buyers:', error);
        }
    };

    const handleUserClick = (user) => {
        setSelectedUser(user);
        fetchOrdersByUser(user.id);
    };

    return (
        <div className="orders-container">
            <h2>Users</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Username</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>
                                <a href="#" onClick={() => handleUserClick(user)}>{user.username}</a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {/* ostatak va≈°eg JSX koda ostaje nepromijenjen */}
            {/* ... */}
        </div>
    );
}

export default AllOrders;
