import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Dohvatite sve proizvode iz baze podataka
    axios.get('http://localhost:8080/products')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <div>
      <h2>Popis proizvoda</h2>
      {products.map(product => (
        <div key={product.id}>
          <h3>{product.name}</h3>
          <img src={product.imageURL} alt={product.name} />
          <p>{product.description}</p>
          <p>Veliƒçina: {product.size.name}</p>
          <p>Cijena: {product.price}</p>
          <p>Boja: {product.color.name}</p>
          <p>Spol: {product.gender.name}</p>
          <p>Kategorija: {product.category.name}</p>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
