import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AddProduct.css';

function AddProduct() {
  const [name, setName] = useState('');
  const [imageURL, setImageURL] = useState('');
  const [description, setDescription] = useState('');
  const [sizeOptions, setSizeOptions] = useState([]);
  const [selectedSize, setSelectedSize] = useState('');
  const [price, setPrice] = useState(0);
  const [colorOptions, setColorOptions] = useState([]);
  const [selectedColor, setSelectedColor] = useState('');
  const [genderOptions, setGenderOptions] = useState([]);
  const [selectedGender, setSelectedGender] = useState('');
  const [categoryOptions, setCategoryOptions] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    // Dohvatite opcije za Size iz baze podataka
    axios.get('http://localhost:8080/sizes')
      .then(response => {
        setSizeOptions(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    // Dohvatite opcije za Color iz baze podataka
    axios.get('http://localhost:8080/colors')
      .then(response => {
        setColorOptions(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    // Dohvatite opcije za Gender iz baze podataka
    axios.get('http://localhost:8080/genders')
      .then(response => {
        setGenderOptions(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    // Dohvatite opcije za Category iz baze podataka
    axios.get('http://localhost:8080/productscategory')
      .then(response => {
        setCategoryOptions(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    const selectedColorObj = colorOptions.find(color => color.id === parseInt(selectedColor));
    const selectedGenderObj = genderOptions.find(gender => gender.id === parseInt(selectedGender));
    const selectedCategoryObj = categoryOptions.find(category => category.id === parseInt(selectedCategory));

    const productData = {
      name,
      imageURL,
      description,
      size: selectedSize,
      price,
      color: selectedColorObj,
      gender: selectedGenderObj,
      category: selectedCategoryObj,
    };

    axios
      .post('http://localhost:8080/products', productData)
      .then((response) => {
        console.log(response.data);
        alert('Proizvod je uspješno dodan.');
        // Resetirajte formu
        setName('');
        setImageURL('');
        setDescription('');
        setSelectedSize('');
        setPrice(0);
        setSelectedColor('');
        setSelectedGender('');
        setSelectedCategory('');
      })
      .catch((error) => {
        console.error(error);
        alert('Došlo je do pogreške prilikom dodavanja proizvoda.');
      });
  };

  return (
    <div className="form-container">
      <h2>Dodaj proizvod</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label>Naziv:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>URL slike:</label>
          <input type="text" value={imageURL} onChange={(e) => setImageURL(e.target.value)} />
        </div>
        <div>
          <label>Opis:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <label>Veličina:</label>
          <select value={selectedSize} onChange={(e) => setSelectedSize(e.target.value)}>
            <option value="">Odaberi veličinu</option>
            {sizeOptions.map(size => (
              <option key={size.id} value={size.id}>{size.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Cijena:</label>
          <input type="number" value={price} onChange={(e) => setPrice(parseInt(e.target.value))} />
        </div>
        <div>
          <label>Boja:</label>
          <select value={selectedColor} onChange={(e) => setSelectedColor(e.target.value)}>
            <option value="">Odaberi boju</option>
            {colorOptions.map(color => (
              <option key={color.id} value={color.id}>{color.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Spol:</label>
          <select value={selectedGender} onChange={(e) => setSelectedGender(e.target.value)}>
            <option value="">Odaberi spol</option>
            {genderOptions.map(gender => (
              <option key={gender.id} value={gender.id}>{gender.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Kategorija:</label>
          <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
            <option value="">Odaberi kategoriju</option>
            {categoryOptions.map(category => (
              <option key={category.id} value={category.id}>{category.name}</option>
            ))}
          </select>
        </div>
        <button type="submit">Dodaj</button>
      </form>
    </div>
  );
}

export default AddProduct;
