import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [isModalOpen, setModalOpen] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false); // Ovdje možete postaviti logiku za provjeru da li je korisnik administrator
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: 0
  });

  useEffect(() => {
    // Povlačenje podataka o proizvodima iz baze podataka ili nekog drugog izvora
  }, []);

  const openModal = (product) => {
    setModalOpen(true);
    setEditingProduct(product);
    if (product) {
      setNewProduct({
        name: product.name,
        description: product.description,
        price: product.price
      });
    } else {
      setNewProduct({
        name: "",
        description: "",
        price: 0
      });
    }
  };

  const closeModal = () => {
    setModalOpen(false);
    setEditingProduct(null);
  };

  const handleEditSubmit = async (event) => {
    event.preventDefault();
    // Ovdje dodajte logiku za uređivanje proizvoda
    closeModal();
  };

  const handleAddProduct = async (event) => {
    event.preventDefault();
    // Ovdje dodajte logiku za dodavanje novog proizvoda
    closeModal();
  };

  return (
    <div>
      <h2>Product List</h2>

      {isAdmin && (
        <button onClick={() => openModal(null)}>Add New Product</button>
      )}

      {products.map((product) => (
        <div key={product.id}>
          <h3>{product.name}</h3>
          <p>{product.description}</p>
          <p>{product.price}</p>

          {isAdmin && (
            <button onClick={() => openModal(product)}>Edit</button>
          )}
        </div>
      ))}

      {isModalOpen && (
        <div className="modal">
          <form onSubmit={editingProduct ? handleEditSubmit : handleAddProduct}>
            <input
              type="text"
              placeholder="Product Name"
              value={newProduct.name}
              onChange={(event) => setNewProduct({ ...newProduct, name: event.target.value })}
            />
            <input
              type="text"
              placeholder="Description"
              value={newProduct.description}
              onChange={(event) => setNewProduct({ ...newProduct, description: event.target.value })}
            />
            <input
              type="number"
              placeholder="Price"
              value={newProduct.price}
              onChange={(event) => setNewProduct({ ...newProduct, price: event.target.value })}
            />
            <button type="submit">Save</button>
            <button type="button" onClick={closeModal}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default ProductList;
