import React, { useState, useEffect } from "react";
import axios from 'axios';
import authHeader from './services/auth-header';

const Cart = () => {
  const [cart, setCart] = useState(null);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    if(user && user.id) {
      axios.get(`http://localhost:8080/orders/${user.id}`, { headers: authHeader() })
        .then(response => {
          setCart(response.data);
        })
        .catch(error => {
          console.error('Error fetching the cart', error);
        });
    }
  }, [user]);

  if (!cart) {
    return <p>Loading...</p>;
  }
  if (!cart || !cart.orderDetails) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>Your Cart</h2>
      <h3>Order Date: {new Date(cart.orderDate).toLocaleDateString()}</h3>
      <ul>
        {cart.orderDetails.map((item) => (
          <li key={item.id}>
            <h4>Product: {item.product.name}</h4>
            <p>Quantity: {item.quantity}</p>
            <p>Price per unit: {item.pricePerUnit}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Cart;
