import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import authHeader from '../services/auth-header';

function ProductDetails() {
  const [product, setProduct] = useState(null);
  const [cart, setCart] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/products/${id}`, {
          headers: authHeader(),
        });
        setProduct(response.data);
      } catch (error) {
        console.error('There was an error fetching the product data:', error);
      }
    };
    fetchData();
  }, [id]);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  if (!product) {
    return <h1>Loading...</h1>;
  }

  return (
    <div className="product-details">
      <h1>{product.name}</h1>
      <img 
        src={`http://localhost:8080/products/images/${product.imageURL}`}
        alt={`Slika proizvoda ${product.name}`}
        className="product-details-image"
      />
      <p>{product.description}</p>
      <p>Veličina: {product.size?.name}</p>
      <p>Cijena: {product.price}€</p>
      <p>Boja: {product.color?.name}</p>
      <p>Spol: {product.gender?.name}</p>
      <button onClick={() => addToCart(product)}>Dodaj u košaricu</button>
    </div>
  );
}

export default ProductDetails;
