import React, { useState, useEffect } from 'react';
import axios from 'axios';
import placeholderImage from './images/placeholder-image.png';
import './ProductList.css';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [orderItems, setOrderItems] = useState([]);
  const [categories, setCategories] = useState([]);

  const userId = 1;

  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:8080/products');
      setProducts(response.data);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dohvaćanja proizvoda.');
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get('http://localhost:8080/categories');
      setCategories(response.data);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dohvaćanja kategorija.');
    }
  };

  const addToOrder = (productId) => {
    setOrderItems([...orderItems, productId]);
    alert(`Proizvod s ID-om ${productId} dodan u narudžbu.`);
  };

  const handleSubmitOrder = async () => {
    const orderData = {
      orderTime: new Date().toISOString(),
      userId: userId,
    };
  
    try {
      const orderResponse = await axios.post('http://localhost:8080/orders', orderData);
  
      for (const itemId of orderItems) {
        const product = products.find((product) => product.id === itemId);
  
        const orderDetailsData = {
          orderId: orderResponse.data.id,
          productId: itemId,
          quantity: 1,
          price: product?.price,
        };
  
        await axios.post('http://localhost:8080/orderDetails', orderDetailsData);
      }
  
      setOrderItems([]);
  
      alert(`Narudžba je uspješno izvršena.`);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom izvršavanja narudžbe.');
    }
  };
  
  return (
    <div>
      <h2>Popis proizvoda</h2>
      <div className="category-list">
        {categories.map((category) => (
          <button key={category.id}>{category.name}</button>
        ))}
      </div>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <h3>{product.name}</h3>
            <img
              src={`${process.env.PUBLIC_URL}/img/${product.image}`}
              alt={product.name}
              className="product-image"
            />
            <p>{product.description}</p>
            <p>Veličina: {product.size?.name}</p>
            <p>Cijena: {product.price}</p>
            <p>Boja: {product.color?.name}</p>
            <p>Spol: {product.gender?.name}</p>
            <p>Kategorija: {product.category?.name}</p>
            <button onClick={() => addToOrder(product.id)}>Dodaj u narudžbu</button>
          </div>
        ))}
      </div>
      {orderItems.length > 0 && (
        <button onClick={handleSubmitOrder}>Izvrši narudžbu</button>
      )}
    </div>
  );
}

export default ProductList;
