import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from './services/auth-header';

function AllOrders() {

    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
  
    useEffect(() => {
      fetchAllUsers();
    }, []);
  
    const fetchAllUsers = async () => {
      try {
        const response = await axios.get('http://localhost:8080/orderDetails/users', { // Pretpostavljamo da je ovo endpoint za dohvaćanje svih korisnika
          headers: authHeader(),
        });
        setUsers(response.data);
      } catch (error) {
        console.error('Došlo je do pogreške prilikom dohvaćanja korisnika:', error);
        setError(error.message);
      }
    };
  
    const fetchOrdersByUser = async (userId) => {
      try {
        const response = await axios.get(`http://localhost:8080/orderDetails/user/${userId}`, { // Pretpostavljamo da je ovo endpoint za dohvaćanje narudžbi određenog korisnika
          headers: authHeader(),
        });
        setOrders(response.data);
      } catch (error) {
        console.error('Došlo je do pogreške prilikom dohvaćanja narudžbi korisnika:', error);
        setError(error.message);
      }
    };
  
    const handleUserClick = (userId) => {
      setSelectedUser(userId);
      fetchOrdersByUser(userId);
    };
  
    return (
        <div>
          <h2>Popis korisnika</h2>
          <div className="user-list">
            {users.map((user) => (
              <div key={user.id} className="user-item">
                <a href="#" onClick={() => handleUserClick(user.id)}>{user.username}</a>
              </div>
            ))}
          </div>
          {selectedUser && (
            <div>
              <h3>Narudžbe korisnika {selectedUser}</h3>
              <div className="order-list">
                {orders.length > 0 ? (
                  orders.map((order) => (
                    <div key={order.id} className="order-item">
                      <h4>Narudžba ID: {order.id}</h4>
                      <p>Proizvod: {order.productName}</p>
                      <p>Količina: {order.quantity}</p>
                      <p>Cijena: {order.price}</p>
                    </div>
                  ))
                ) : (
                  <p>Nema još narudžbi.</p>
                )}
              </div>
            </div>
          )}
        </div>
      );
  }
  
  export default AllOrders;
