import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from '../services/auth-header';
import '../AllOrders.css';

function AllOrders() {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [orders, setOrders] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [topSpenders, setTopSpenders] = useState([]);
    const [topBuyers, setTopBuyers] = useState([]);

    useEffect(() => {
        fetchAllUsers();
        fetchTopSpenders();
        fetchTopBuyers();
    }, []);

    useEffect(() => {
  // Pretpostavimo da fetchAllUsers() postavlja 'users' state
  fetchAllUsers().then(fetchedUsers => {
    Promise.all(
      fetchedUsers.map(user => 
        // Ovdje dohvatite sve narudžbe za svakog korisnika
        fetchOrdersByUser(user.id).then(orders => {
          // Izračunajte ukupnu potrošnju za korisnika
          const totalSpent = orders.reduce((acc, order) => {
            return acc + order.totalPrice; // Ovdje pretpostavljamo da svaka narudžba ima ukupnu cijenu
          }, 0);

          // Dodajte ukupnu potrošnju kao novo polje u objekt korisnika
          return {...user, totalSpent};
        })
      )
    ).then(usersWithTotalSpent => {
      // Sortirajte korisnike po ukupnoj potrošnji
      usersWithTotalSpent.sort((a, b) => b.totalSpent - a.totalSpent);

      // Postavite novi state
      setUsers(usersWithTotalSpent);
    });
  });

}, []);

    const fetchAllUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/users', {
                headers: authHeader(),
            });
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const fetchOrdersByUser = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8080/orderDetails/details/user/${userId}`, {
                headers: authHeader(),
            });
            setOrders(response.data);

            const total = response.data.reduce((acc, orderDetail) => acc + (orderDetail.quantity * orderDetail.product.price), 0);
            setTotalPrice(total);
        } catch (error) {
            console.error('Error fetching user orders:', error);
        }
    };

    const fetchTopSpenders = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/top-spending-users', {
                headers: authHeader(),
            });
            setTopSpenders(response.data);
        } catch (error) {
            console.error('Error fetching top spenders:', error);
        }
    };

    const fetchTopBuyers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/top-selling-products', {
                headers: authHeader(),
            });
            setTopBuyers(response.data);
            console.log('Top Buyers:', response.data);
            
        } catch (error) {
            console.error('Error fetching top buyers:', error);
        }
    };

    const handleUserClick = (user) => {
        setSelectedUser(user);
        setOrders([]);
        setTotalPrice(0);
        
        fetchOrdersByUser(user.id);
    };

    return (
        <div className="orders-container">
            <h2>Users</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Username</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>
                                <a href="#" onClick={() => handleUserClick(user)}>{user.username}</a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Top Spenders</h2>
            <table className="users-table">
                <thead>
                <tr>
                    <th>Username</th>
                    <th>Email</th>
                    <th>Total Spent</th>
                </tr>
            </thead>
            <tbody>
            {topSpenders.map((user) => (
                    <tr key={user.id}>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td>{user.totalSpent} €</td>
                </tr>
            ))}
            </tbody>
            </table>

            <h2>Best selling products</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Products</th>
                        <th>Images</th>
                    </tr>
                </thead>
                <tbody>
                 {topBuyers.map((product) => (
                    <tr key={product.id}>
                    <td>{product.name}</td>
                    <td>{<img src={`http://localhost:8080/products/images/${product.imageURL}`} alt={product.name} width="100" />}</td>
          
                    </tr>
                ))}
                </tbody>
                </table>

            {selectedUser && (
                <div>
                    <h3>Orders of {selectedUser.username}</h3>
                    <table className="orders-table">
                        <thead>
                            <tr>
                                <th>Product Image</th>
                                <th>Product Name</th>
                                <th>Quantity</th>
                                <th>Price</th>
                                <th>Date of Order</th>
                            </tr>
                        </thead>
                        <tbody>
                            {orders.map((orderDetail) => (
                                <tr key={orderDetail.id}>
                                    <td><img src={`http://localhost:8080/products/images/${orderDetail.product.imageURL}`} alt={orderDetail.product.name} width="100" /></td>
                                    <td>{orderDetail.product.name}</td>
                                    <td>{orderDetail.quantity}</td>
                                    <td>{orderDetail.product.price} €</td>
                                    <td>{new Date(orderDetail.order.orderTime).toLocaleString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <strong><p>Total Price: {totalPrice} €</p></strong>
                </div>
            )}
        </div>
    );
}

export default AllOrders;
