// UserOrders.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';  // Ako koristite axios za HTTP zahteve
import AuthService from "../services/auth.service";

const UserOrders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Pretpostavimo da imate API krajnju tačku na `/api/orders` koja vraća narudžbe za trenutnog korisnika
    const user = AuthService.getCurrentUser();
    if (user) {
      axios.get(`/api/orders?userId=${user.id}`)
        .then(response => {
          setOrders(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching orders!", error);
        });
    }
  }, []);

  return (
    <div>
      <h2>Your Past Orders</h2>
      {orders.length === 0 ? (
        <p>You have no past orders.</p>
      ) : (
        <ul>
          {orders.map((order, index) => (
            <li key={index}>
              <div>
                Order ID: {order.id} <br />
                Product: {order.productName} <br />
                Quantity: {order.quantity} <br />
                Date: {new Date(order.dateOrdered).toLocaleDateString()}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default UserOrders;
