import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from '../services/auth-header';
import AuthService from '../services/auth.service';
import { Link } from 'react-router-dom';
import '../AllOrders.css';

function MyOrders() {
    const [orders, setOrders] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);

    useEffect(() => {
        fetchMyOrders();
    }, []);

    const fetchMyOrders = async () => {
        const user = AuthService.getCurrentUser();

        if (user) {
            try {
                const response = await axios.get(`http://localhost:8080/orderDetails/details/user/${user.id}`, {
                    headers: authHeader(),
                });
                setOrders(response.data);

                const total = response.data.reduce((acc, orderDetail) => acc + (orderDetail.quantity * orderDetail.product.price), 0);
                setTotalPrice(total);
            } catch (error) {
                console.error('Error fetching my orders:', error);
            }
        }
    };

    const uniqueOrders = orders.reduce((acc, current) => {
        const found = acc.find(order => order.order.id === current.order.id);
        if (!found) {
          return acc.concat([current]);
        } else {
          return acc;
        }
      }, []);
  
      return (
          <div className="orders-container">
              <h2>My Orders</h2>
              {uniqueOrders.length === 0 ? (
                  <p>You haven't placed any orders yet.</p>
              ) : (
                  <table className="orders-table">
                      <thead>
                          <tr>
                              <th>Order ID</th>
                              <th>Product Name</th>
                              <th>Quantity</th>
                              <th>Price</th>
                              <th>Date of Order</th>
                          </tr>
                      </thead>
                      <tbody>
                          {uniqueOrders.map((orderDetail) => (
                              <tr key={orderDetail.id}>
                                  <td><Link to={`/orderDetails/${orderDetail.order.id}`}>{orderDetail.order.id}</Link></td>
                                  <td>{new Date(orderDetail.orderTime).toLocaleString()}</td>
                              </tr>
                          ))}
                      </tbody>
                  </table>
              )}
              <strong><p>Total Price: {totalPrice} â‚¬</p></strong>
          </div>
      );
}

export default MyOrders;
