import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";

class Profile extends Component {
  state = {
    redirect: null,
    userReady: false,
    currentUser: { username: "" }
  };

  componentDidMount() {
    const currentUser = AuthService.getCurrentUser();

    if (!currentUser) {
      this.setState({ redirect: "/home" });
    } else {
      this.setState({ currentUser: currentUser, userReady: true });
    }
  }

  render() {
    const { redirect, userReady, currentUser } = this.state;

    if (redirect) {
      return <Navigate to={redirect} />;
    }

    const containerStyle = {
      maxWidth: "800px",
      margin: "auto",
      padding: "20px",
      boxShadow: "0 0 10px rgba(0,0,0,0.1)"
    };

    const jumbotronStyle = {
      padding: "2rem 1rem",
      marginBottom: "2rem",
      backgroundColor: "#f9f9f9",
      borderRadius: "0.3rem"
    };

    const headerStyle = {
      fontSize: "1.5rem"
    };

    return (
      <div style={containerStyle}>
        {userReady && (
          <div>
            <header style={jumbotronStyle}>
              <h3 style={headerStyle}>Bok, {currentUser.username}!</h3>
            </header>
            <p>
              Dobrodošli u <b>Pien!</b>
            </p>
            <p>Drago nam je da si tu :)</p>
            <ul>
              <li>Pregledajte najnovije proizvode</li>
              <li>Pregledajte prethodne narudžbe</li>
              <li>Istražite nešto novo</li>
              <li>Uživajte u online kupnji!</li>
            </ul>
          </div>
        )}
      </div>
    );
  }
}

export default Profile;
